    // seeder for animal info
    trait SeederAnimalInfo {
        // to get animal info
        public function animal_info(string $animalType = 'dog', string $detailType = 'name', string $getList = 'no') {
            // determine if we have a viable animal type
            if (!in_array($animalType, $this->animalTypes)) {
                return "Animal type of \"{$animalType}\" is not supported";
            }

            // get available animal types
            if ($animalType == 'getTypes') {
               return implode(",", $this->animalTypes);
            }

            // check and see if details are available, if so return them
            $toGet = $animalType . $detailType;
            // check to see if they want to get the whole list or just one element of the list
            if (strtolower($getList) == 'yes') {
                return $this->$toGet;
            }
            // get one element of the list or return error
            if (isset($this->$toGet)) {
                return $this->$toGet[rand(0, count($this->$toGet) - 1)];
            } else {
                return "Animal detail of \"{$detailType}\" is not supported";
            }
        }

        // Animal Types arrays
        public $animalTypes = ['dog', 'horse', 'cat', 'bird'];

